DROP TABLE IF EXISTS `crawler_config`;
CREATE TABLE `crawler_config` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `Name` varchar(255) NOT NULL DEFAULT '' COMMENT '爬虫配置名称',
  `SubmitUrl` varchar(1025) NOT NULL DEFAULT '' COMMENT '提交的url',
  `KeyWordField` varchar(255) NOT NULL DEFAULT '' COMMENT '关键字标识',
  `PageField` varchar(255) NOT NULL DEFAULT '' COMMENT '页码标识',
  `Fields` varchar(1024) NOT NULL DEFAULT '' COMMENT '字段，同时对应配置里面的字段',
  `AllRowConfig` varchar(4096) NOT NULL DEFAULT '' COMMENT '所有记录配置',
  `MaxPage` int(11) NOT NULL DEFAULT '100' COMMENT '最大页码',
  `StopPageCount` int(11) NOT NULL DEFAULT '3' COMMENT '失败多少次跳过任务',
  `TableName` varchar(255) NOT NULL DEFAULT '' COMMENT '表名称',
  `AddTime` int(11) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `AddUID` int(11) NOT NULL DEFAULT '0' COMMENT '添加人Id',
  `UpdateTime` int(11) NOT NULL COMMENT '更新时间',
  `Status` int(11) NOT NULL DEFAULT '0' COMMENT '状态',
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

